<?php


namespace Tests\PicPay\Responses;

use Carbon\Carbon;
use Orchestra\Testbench\TestCase;
use PicPay\Responses\CheckoutResponse;

class CheckoutResponseTest extends TestCase
{
    function testParse()
    {
        $response = new CheckoutResponse('
            {
              "referenceId": "102030",
              "paymentUrl": "https://app.picpay.com/checkout/NWFmMGRjNmViZDc0Y2EwMDMwNzZlYzEw",
              "expiresAt": "2022-05-01T16:00:00-03:00",
              "qrcode": {
                "content": "https://app.picpay.com/checkout/NWNlYzMxOTM1MDg1NGEwMDIwMzUxODcy",
                "base64": "data:image/png;base64,"
              }
            }
        ');

        self::assertEquals('102030', $response->getReferenceId());
        self::assertEquals('https://app.picpay.com/checkout/NWFmMGRjNmViZDc0Y2EwMDMwNzZlYzEw', $response->getPaymentUrl());
        self::assertEquals(Carbon::parse('2022-05-01T16:00:00-03:00'), $response->getExpiresAt());
        self::assertEquals('https://app.picpay.com/checkout/NWNlYzMxOTM1MDg1NGEwMDIwMzUxODcy', $response->getQrcode()->getContent());
    }
}